import React, { useState } from 'react';
import { View, Text } from 'react-native';
import TextInputField from './textInputField';
import PickerField from '../register/pickerField';
import RegisterPressable from '../register/RegisterPressable';
import { useForm, Controller } from 'react-hook-form';
/**
 * check if fields & userTypeOptions are not null
 */
export default function Form({ fields, userTypeOptions }) {
  const { control, handleSubmit, setValue, errors } = useForm();
  const [userDetails, setUserDetails] = useState({
    username: '',
    firstName: '',
    lastName: '',
    phoneNumber: '',
    password: '',
  });
  const [userType, setUserType] = useState('');

  const handleFieldChange = (field, value) => {
    setUserDetails((prevDetails) => ({ ...prevDetails, [field]: value }));
  };

  const handleRegister = () => {
    // Implement registration logic here
    //check validations / wran user
    //save data / send to server
    console.log('Registering user:', { ...userDetails, userType });
  };

  return (
    <View>
      {fields.map((field) => (
        <View key={field.label}>
          <Text>{field.label}</Text>
          <Controller
            control={control}
            render={({ onChange, onBlur, value }) => (
              <TextInput
                onBlur={onBlur}
                onChangeText={(text) => onChange(text)}
                value={value}
                placeholder={field.placeholder}
                secureTextEntry={field.secureTextEntry}
              />
            )}
            name={field.state}
            rules={{ required: `Please enter your ${field.state}` }}
            defaultValue=""
          />
          {errors[field.state] && <Text>{errors[field.state].message}</Text>}
        </View>
      ))}
      <Text>User Type</Text>
      <Controller
        control={control}
        render={({ onChange, value }) => (
          <PickerField
            selectedValue={value}
            onValueChange={(itemValue) => onChange(itemValue)}
            items={userTypeOptions}
          />
        )}
        name="userType"
        rules={{ required: 'Please select user type' }}
        defaultValue=""
      />
      {errors.userType && <Text>{errors.userType.message}</Text>}
      <RegisterPressable onPress={handleSubmit(handleRegister)} title="Register" />
    </View>
  );
};
