import React from 'react';
import { View, TouchableOpacity, Text } from 'react-native';
import { openComposer } from 'react-native-email-link';
import { Platform } from 'react-native';

const SendEmail = () => {
    const openMailbox = async (email, options) => {
        // ...
      
        if (Platform.OS === 'ios' && typeof ActionSheetIOS !== 'undefined') {
          // Use ActionSheetIOS only on iOS and when it's defined
          await ActionSheetIOS.showActionSheetWithOptions(
            {
              options: options,
              cancelButtonIndex: options.length - 1,
            },
            (buttonIndex) => {
              // Handle button press
              // ...
            }
          );
        }
      };
  const handleEmailPress = () => {
    const to = ['glowing123456@gmail.com']; // specify the recipient email address
    const subject = 'Subject of the email';
    const body = 'Body of the email';

    // Open the default email client with pre-filled information
    openComposer({
      to,
      subject,
      body,
    });
  };

  return (
    <View>
      <TouchableOpacity onPress={handleEmailPress}>
        <Text>Send Email</Text>
      </TouchableOpacity>
    </View>
  );
};

export default SendEmail;
