import React from 'react';
import { View } from 'react-native';
import Form from '../shared/form';
import GenericForm from './f';

const fields = [
  { name: 'User Name', state: 'username', placeholder: 'Enter your email',type: 'text',rules: { required: 'Email is required.', pattern: { value: /\S+@\S+\.\S+/, message: 'Email is invalid.' } } },
  { name: 'First Name', state: 'firstName', placeholder: 'Enter your firstName',type: 'text' ,rules: { required: 'First Name is required.'} },
  { name: 'Last Name', state: 'lastName', placeholder: 'Enter your lastName',type: 'text' ,rules: { required: 'Last Name is required.'} },
  { name: 'Phone Number', state: 'phoneNumber', placeholder: 'Enter your phoneNumber',type: 'text' ,rules: { required: 'phoneNumber is required.'} },
  { name: 'Password', state: 'password', placeholder: 'Enter your password',type: 'text', secureTextEntry: true ,rules: { required: 'password is required.'} },
  { name: 'Type', state: 'type', options: userTypeOptions,type: 'picker', rules: { required: 'type is required.'} },
];



export default function RegistrationForm() {
  const onSubmit = (data) => {
    // Handle form submission logic
    //save the data in db
    console.log('Form data:', data);
  };
  return (
    <View>
      {/* user userDetails should come back from the form (as an event?)
    check if fields & userTypeOptions are not null */}
      {/* <Form fields={fields} userTypeOptions={userTypeOptions} ></Form> */}
      <GenericForm fields={fields} onSubmit={onSubmit}></GenericForm>
    </View>
  );
}
