import React, { useState } from 'react';
import { View, Text, TextInput, Button } from 'react-native';
import { useForm, Controller } from 'react-hook-form';
import PickerField from '../register/pickerField';
import RegisterPressable from '../register/RegisterPressable';



const RegistrationF = ({ fields, userTypeOptions }) => {
  const { control, handleSubmit, setValue, errors } = useForm();
  const [userDetails, setUserDetails] = useState({
    username: '',
    firstName: '',
    lastName: '',
    phoneNumber: '',
    password: '',
  });
  const [userType, setUserType] = useState('');
  const handleRegister = (data) => {
    // Implement registration logic here
    // Check validations / warn user
    // Save data / send to server
    console.log('Registering user:', data);
  };

  return (
    <View>
      {fields.map((field) => (
        <View key={field.label}>
          <Text>{field.label}</Text>
          <Controller
            control={control}
            render={({ onChange, onBlur, value }) => (
              <TextInput
                onBlur={onBlur}
                onChangeText={(text) => onChange(text)}
                value={userDetails[field.state]}
                placeholder={field.placeholder}
                secureTextEntry={field.secureTextEntry}
              />
            )}
            name={field.state}
            rules={{ required: `Please enter your ${field.label.toLowerCase()}` }}
            defaultValue=""
                
            />
          {errors[field.state] && <Text>{errors[field.state].message}</Text>}
        </View>
      ))}
      <Text>User Type</Text>
      <Controller
        control={control}
        render={({ onChange, value }) => (
          <PickerField
            selectedValue={value}
            onValueChange={(itemValue) => onChange(itemValue)}
            items={userTypeOptions}
          />
        )}
        name="userType"
        rules={{ required: 'Please select user type' }}
        defaultValue=""
      />
      {errors.userType && <Text>{errors.userType.message}</Text>}
      <RegisterPressable onPress={handleSubmit(handleRegister)} title="Register" />
    </View>
  );
};

export default RegistrationF;
