import React from 'react';
import { View, TextInput, TouchableOpacity, Text, StyleSheet } from 'react-native';
import { useForm, Controller } from 'react-hook-form';

const GenericForm = ({ fields, onSubmit }) => {
  const { control, handleSubmit, setValue, formState: { errors } } = useForm();

  const handleInputChange = (field, text) => {
    setValue(field, text);
  };

  return (
    <View style={styles.container}>
      {fields.map((field) => (
        <View key={field.label}>
          <Controller
            control={control}
            render={({ field: { value } }) => (
              <TextInput
                style={styles.input}
                placeholder={field.placeholder}
                value={value}
                onChangeText={(text) => handleInputChange(field.label, text)}
                secureTextEntry={field.secureTextEntry}
              />
            )}
            name={field.label}
            rules={field.rules}
            defaultValue=""
          />

          {errors[field.label] && <Text style={styles.error}>{errors[field.label].message}</Text>}
        </View>
      ))}

      <TouchableOpacity
        style={[styles.button, { opacity: Object.keys(errors).length === 0 ? 1 : 0.5 }]}
        disabled={Object.keys(errors).length > 0}
        onPress={handleSubmit(onSubmit)}
      >
        <Text style={styles.buttonText}>Submit</Text>
      </TouchableOpacity>

      {/* Display error messages */}
      {Object.values(errors).map((error, index) => (
        <Text key={index} style={styles.error}>
          {error.message}
        </Text>
      ))}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 16,
    justifyContent: 'center',
  },
  input: {
    height: 60,
    borderColor: '#ccc',
    borderWidth: 1,
    marginBottom: 12,
    paddingHorizontal: 10,
    borderRadius: 8,
    fontSize: 16,
  },
  button: {
    backgroundColor: 'green',
    borderRadius: 8,
    paddingVertical: 10,
    alignItems: 'center',
    marginTop: 16,
    marginBottom: 12,
  },
  buttonText: {
    color: '#fff',
    fontWeight: 'bold',
    fontSize: 16,
  },
  error: {
    color: 'red',
    fontSize: 20,
    marginBottom: 12,
  },
});

export default GenericForm;
