import { View, Button, Text } from 'react-native';
import { AudioRecorder, AudioUtils } from 'react-native-audio-recorder-player'; // ייתכן ויש להתאים את היבוא לספריה שאת משתמשת בה
import { useState } from 'react';
const RecordAudio = () => {
  const [isRecording, setIsRecording] = useState(false);
  const [audioPath, setAudioPath] = useState('');
  const startRecording = async () => {
    const path = AudioUtils.D + 'C:/test.aac'; // ניתן להתאים את הנתיב ושם הקובץ לפי הצורך
    try {
      await AudioRecorder.startRecording(path);
      setAudioPath(path);
      setIsRecording(true);
    } catch (err) {
      console.error('Failed to start recording', err);
    }
  };
  const stopRecording = async () => {
    try {
      const filePath = await AudioRecorder.stopRecording();
      setIsRecording(false);
      console.log('Recording stopped, file path is: ', filePath);
    } catch (err) {
      console.error('Failed to stop recording', err);
    }
  };
  const toggleRecording = () => {
    if (isRecording) {
      stopRecording();
    } else {
      startRecording();
    }
  };
  return (
    <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
      <Button
        title={isRecording ? 'Stop Recording' : 'Start Recording'}
        onPress={toggleRecording}
      />
      {audioPath !== '' && (
        <Text style={{ marginTop: 20 }}>Recorded audio path: {audioPath}</Text>
      )}
    </View>
  );
};
export default RecordAudio;
