import React from 'react';
import { View, TextInput, Text, StyleSheet, Pressable,Picker  } from 'react-native';
import { useForm, Controller } from 'react-hook-form';
import GenericButton from '../shared/button';

const GenericForm = ({ fields, onSubmit }) => {
    const { control, handleSubmit, setValue, formState: { errors } } = useForm();

    const handleInputChange = (field, text) => {
        setValue(field, text);
    };
console.log(fields);
    return (
        <View style={styles.container}>
            {fields.map((field) => (
                <View key={field.name}>
                    {field.type === 'text' && (
                        <Controller
                            control={control}
                            render={({ field: { value } }) => (
                                <TextInput
                                    style={styles.input}
                                    placeholder={field.placeholder}
                                    value={value}
                                    onChangeText={(text) => handleInputChange(field.name, text)}
                                    secureTextEntry={field.secureTextEntry}
                                />
                            )}
                            name={field.name}
                            rules={field.rules}
                            defaultValue=""
                        />
                    )}
                    {field.type === 'picker' && field.options && (
                        <Controller
                            control={control}
                            render={({ field: { value } }) => (
                                <Picker
                                    selectedValue={value}
                                    onValueChange={(itemValue) => setValue(field.name, itemValue)}
                                >
                                    {field.options.map((option) => (
                                        <Picker.Item key={option.value} label={option.name} value={option.value} />
                                    ))}
                                </Picker>
                            )}
                            name={field.name}
                            rules={field.rules}
                            defaultValue=""
                        />
                    )}
                    {errors[field.name] && <Text style={styles.error}>{errors[field.name].message}</Text>}
                </View>
            ))}
            {/* i do want the buttons to be generic */}
            {/* <Pressable style={[styles.button, { opacity: Object.keys(errors).length === 0 ? 1 : 0.5 }]}
                disabled={Object.keys(errors).length > 0}
                onPress={handleSubmit(onSubmit)}
                title="Submit/Register" >
                <Text style={styles.buttonText}>I'm pressable!</Text>
            </Pressable> */}
            <GenericButton></GenericButton>
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        padding: 16,
        justifyContent: 'center',
    },
    input: {
        height: 60,
        borderColor: '#ccc',
        borderWidth: 1,
        marginBottom: 12,
        paddingHorizontal: 10,
        borderRadius: 8,
        fontSize: 16,
    },
    button: {
        backgroundColor: 'green',
        borderRadius: 8,
        paddingVertical: 10,
        alignItems: 'center',
        marginTop: 16,
        marginBottom: 12,
    },
    buttonText: {
        color: '#fff',
        fontWeight: 'bold',
        fontSize: 16,
    },
    error: {
        color: 'red',
        fontSize: 20,
        marginBottom: 12,
    },
});

export default GenericForm;
