    console.log('Registering user:', { ...userDetails, userType });
  };

  return (
    <View>
      {fields.map((field) => (
        <View key={field.label}>
          <Text>{field.label}</Text>

          <Controller
            control={control}
            render={({ onChange, onBlur, value }) => (

          <TextInputField
            value={userDetails[field.state]}
            onChangeText={(value) => handleFieldChange(field.state, value)}
            placeholder={field.placeholder}
            secureTextEntry={field.secureTextEntry}
          />

          )}
            name={field.state}
            rules={{ required: `Please enter your ${field.label.toLowerCase()}` }}
            defaultValue=""
          />

        </View>
      ))}
      {/* finish making it generic */}
      <Text>User Type</Text>
      <PickerField
        selectedValue={userType}
        onValueChange={(itemValue) => setUserType(itemValue)}
        items={userTypeOptions}
      />
      <RegisterPressable onPress={handleRegister} title="Register" />
    </View>
  );
}
