import React from 'react';
import { View, TouchableOpacity, Text } from 'react-native';
import { openComposer } from 'react-native-email-link';

const SendEmail = () => {
  const handleEmailPress = () => {
    const to = ['recipient@example.com']; // ציין את כתובת הדואר האלקטרוני של הנמען
    const subject = 'נושא המייל';
    const body = 'גוף המייל';

    // פתח את לקוח הדואר האלקטרוני המותקן על המכשיר עם מידע מוקדם
    openComposer({
      to,
      subject,
      body,
    });
  };

  return (
    <View>
      <TouchableOpacity onPress={handleEmailPress}>
        <Text>שלח מייל</Text>
      </TouchableOpacity>
    </View>
  );
};

export default SendEmail;
